import { useSelector, useDispatch } from "react-redux";
import { increment, decrement } from "../store"; // Importar ações geradas pelo slice
import classes from "./Counter.module.css";

const Counter = () => {
  const counter = useSelector((state) => state.counter.counter); // Seleciona o valor do contador no estado global
  const dispatch = useDispatch();

  const incrementHandler = () => {
    dispatch(increment()); // Usa a ação gerada pelo slice
  };

  const decrementHandler = () => {
    dispatch(decrement()); // Usa a ação gerada pelo slice
  };

  const toggleCounterHandler = () => {};

  return (
    <main className={classes.counter}>
      <h1>Redux Counter</h1>
      <div className={classes.value}>{counter}</div>
      <div>
        <button onClick={incrementHandler}>Increment</button>
        <button onClick={decrementHandler}>Decrement</button>
      </div>
      <button onClick={toggleCounterHandler}>Toggle Counter</button>
    </main>
  );
};

export default Counter;
