import { createBrowserRouter, RouterProvider } from "react-router-dom";
import HomePage from "./pages/HomePage";
import EventsPage, {loader as eventsLoader} from "./pages/EventsPage";
import EventDetailPage, {loader as eventDetailLoader, action as deleteEventAction} from "./pages/EventDetailsPage";
import NewEventPage from "./pages/NewEventPage";
import EditEventPage from "./pages/EditEventPage";
import RootLayout from "./pages/Roots";
import EventsRootLayout from "./pages/EventsRoots";
import ErrorPage from "./pages/Error";
import {action as manipulateEventAction} from "./pages/EventForm";

// BONUS: Add another (nested) layout route that adds the <EventNavigation> component above all /events... page components

const router = createBrowserRouter([
  {
    path: "/",
    element: <RootLayout />,
    errorElement: <ErrorPage/>,
    children: [
      { index: true, element: <HomePage /> },
      {
        path: "events",
        element: <EventsRootLayout />,
        children: [
          {
            index: true, element: <EventsPage />, 
            loader: eventsLoader,
          },
          { path: ":eventId", 
            id: "event-detail",
            loader: eventDetailLoader,
            children: [
              {
                index: true,
                element: <EventDetailPage />,
                action:deleteEventAction
              },
              {path: "edit", element: <EditEventPage />, action: manipulateEventAction},
            ]
          },
          { path: "new", element: <NewEventPage />, action: manipulateEventAction},
        ]
      }

    ]
  }
]);

function App() {
  return <div>
    <RouterProvider router={router} />
  </div>;
}

export default App;
